mapper.insert:
INSERT INTO tbl_partner
(
	partner_id
	, partner_pw
	, partner_name
	, comp_name
	, comp_address
	, phone
	, regist_num
	, comp_num
	, regist_date
	, use_at
)
VALUES (
	#{partId}
	, #{partPw}
	, #{partName}
	, #{compName}
	, #{compAddress}
	, #{phone}
	, #{registNum}
	, #{compNum}
	, NOW()
	, 'Y'
)

mapper.update:
UPDATE tbl_partner
SET 
	part_pw = #{partPw}
	, partner_name = #{partName}
	, comp_name = #{compName}
	, phone = #{phone}
	, update_date = now()
WHERE part_no = #{partNo}
