mapper.insert:
INSERT INTO USER
(
	USER_NUM
	, USER_ID
	, USER_PW
	, USER_NAME
	, USER_PHONE
	, USER_EMAIL
	, REGIST_DATE
	, USE_AT
)
VALUES (
	#{userNum}
	, #{userId}
	, #{userPw}
	, #{userNm}
	, #{phone}
	, #{email}
	, NOW()
	, 'Y'
)

mapper.update:
UPDATE USER
SET 
	USER_PW = #{userPw}
	, USER_NAME = #{userNm}
	, USER_EMAIL = #{userEmail}
	, USER_PHONE = #{userPhone}
WHERE USER_NUM = #{userNum}
